import React from 'react';
import Answer from './Answer';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import * as answerActions from '../actions/selectedAnswerActions';
class Options extends React.PureComponent{

	handleClick (valClicked){

		let isCorrect = (valClicked.index+1) === this.props.data.key;
		isCorrect = isCorrect ? 'pass' : 'fail'

	    this.props.actions.selectedAnswer(isCorrect);
		document.getElementById("selectedAnswer").className = isCorrect;
	 }
	render () {
		let options = this.props.data.options;
		var iterator = options.values();
		console.log(this.props.data.options);
        return (
            <div>
	            <div className="col-md-10">

						<div onClick={this.handleClick} className="strong options" key="1" id="selectedAnswer" >
	                    	<h4> 
								1. {iterator.next().value}
							</h4>
	                    </div>

						<div onClick={this.handleClick} className="strong options" key="2" id="selectedAnswer" >
	                    	<h4> 
								2. {iterator.next().value}
							</h4>
	                    </div>

						<div onClick={this.handleClick} className="strong options" key="3" id="selectedAnswer" >
	                    	<h4> 
								3. {iterator.next().value}
							</h4>
	                    </div>

						<div onClick={this.handleClick} className="strong options" key="4" id="selectedAnswer" >
	                    	<h4> 
								4. {iterator.next().value}
							</h4>
	                    </div>
	                
					{options.map((value, index) => {
	                    return <div onClick={this.handleClick.bind(this,{index})} className="strong options" key={index} id="selectedAnswer" >
	                    	<h4> 
								{index+1}. {value}
							</h4>
	                    </div>;
	                  } , this)}
	            </div>
				<Answer classApply={this.props.bgClass}/>
            </div>
        )
	}
}

function mapStateToProps(state, ownProps){
    return {
        bgClass : state.selectedAnswerReducer.bgClass
    };
}

function mapDispatchToProps(dispatch){
    return {
        actions: bindActionCreators(answerActions , dispatch)
    };
}
export default connect(mapStateToProps,mapDispatchToProps)(Options);
